import { useEffect,useState } from "react";
import axios from "axios";
import "./Categories.css";

export const Categories=()=>{
    const [categories, setCategories] = useState([]);
    const [numberOfCategoryToShow,setNumberOfCategoryToShow]=useState(0)
    const handleShowMoreRightClick=()=>{
        setNumberOfCategoryToShow((prev)=>prev+10);
    }

    useEffect(()=>{
        (async ()=>{
            try{
                const {data}=await axios.get("https://travel-app-backend-five.vercel.app/api/category")
                const categoriesToShow=data.slice(numberOfCategoryToShow,numberOfCategoryToShow +10);

                setCategories(categoriesToShow);
            }catch(err){
                console.log(err);
            }
        })()
    },[])
    return(
        <>
            <section className="categories d-flex align-center gap-large cursor-pointer">
                <button>
                    <span class="material-symbols-outlined">chevron_left</span>
                </button>

                
                {
                    
                    categories && categories.map(({category})=>
                    <span>
                        {category}
                    </span>)
                }
                <button onClick={handleShowMoreRightClick}>
                    <span class="material-symbols-outlined">chevron_right</span>
                </button>
                
            </section>
        </>
    );
};
